import React, {Fragment} from "react";
import ToDo from "./ToDo";

export default function ToDoList({tasks, match}) {
    let filterTask
    switch (match.params.filter) {
        case 'completed':
            filterTask = tasks.filter(arg=>arg.completed)
            break;
    
        default:
            filterTask = tasks
            break;
    }

    if(filterTask.length!==0){
        return (
        <Fragment>
            <h1>Liste des tâches</h1>
            <ul>
                {
                    filterTask.map(task => <ToDo key={task.id} task={task} />)
                }
            </ul>
        </Fragment>
        )
    }else{
        return(
            <ul>
                <li>Aucune tâche</li>
            </ul>
        )
    }
}
