import React, { Component, Fragment } from "react"
import Hello from "./Hello"
import Fonction from "./Fonction"
import "./style.css"

class App extends Component{
    render(){
        const nom ="koto"
        const age = 25 
        let stat = false
        const toggle = ()=>{
            stat = !stat
            console.log(stat)
            //document.querySelector('.boite').classList = 'off'
        }
        return(
        <Fragment>
            <Hello nom={nom}/>
            <Fonction age={age}/>
            <button className="btn btn-primary" onClick={toggle}>
                interrupteur
            </button>
            
            <h1> {stat } </h1>
        </Fragment>
        )
    } 
}

export default App
