import React, { Fragment, useState } from 'react'
import s from "../css/inscription.module.css"





const SelectComponent = () => {
    const [selectedMonth, setSelectedMonth] = useState('');
    const [selectedYear, setSelectedYear] = useState('');
    const [selectedDate, setSelectedDate] = useState('');

    const months = [
        { value: '01', label: 'Janvier' },
        { value: '02', label: 'Février' },
        { value: '03', label: 'Mars' },
        { value: '04', label: 'Avril' },
        { value: '05', label: 'Mai' },
        { value: '06', label: 'Juin' },
        { value: '07', label: 'Juillet' },
        { value: '08', label: 'Août' },
        { value: '09', label: 'Septembre' },
        { value: '10', label: 'Octobre' },
        { value: '11', label: 'Novembre' },
        { value: '12', label: 'Décembre' },
    ];

    const years = [];
    const currentYear = new Date().getFullYear();
    for (let year = currentYear; year >= currentYear - 10; year--) {
        years.push(year);
    }

    const handleMonthChange = (event) => {
        setSelectedMonth(event.target.value);
        setSelectedDate('');
    };

    const handleYearChange = (event) => {
        setSelectedYear(event.target.value);
        setSelectedDate('');
    };

    const handleDateChange = (event) => {
        setSelectedDate(event.target.value);
    };

    const generateDates = () => {
        const daysInMonth = new Date(selectedYear, selectedMonth, 0).getDate();
        const dates = [];
        for (let i = 1; i <= daysInMonth; i++) {
            dates.push(i);
        }
        return dates;
    };


    const Inscription = () => {




        return (
            <div>
                <label htmlFor="months">Mois :</label>
                <select id="months" value={selectedMonth} onChange={handleMonthChange}>
                    <option value="">Sélectionnez</option>
                    {months.map((month) => (
                        <option key={month.value} value={month.value}>{month.label}</option>
                    ))}
                </select>

                <label htmlFor="years">Année :</label>
                <select id="years" value={selectedYear} onChange={handleYearChange}>
                    <option value="">Sélectionnez</option>
                    {years.map((year) => (
                        <option key={year} value={year}>{year}</option>
                    ))}
                </select>

                {selectedMonth && selectedYear && (
                    <div>
                        <label htmlFor="dates">Date :</label>
                        <select id="dates" value={selectedDate} onChange={handleDateChange}>
                            <option value="">Sélectionnez</option>
                            {generateDates().map((date) => (
                                <option key={date} value={date}>{date}</option>
                            ))}
                        </select>
                    </div>
                )}

                {selectedDate && (
                    <p>Vous avez sélectionné le {selectedDate}/{selectedMonth}/{selectedYear}</p>
                )}
            </div>
        );




    }
    

}


export default Inscription;
