import React from 'react';
import ToDo from './ToDo'

const ToDoList = ({tasks, match, onToggleCompleted}) =>{
    let filterTask
    switch (match.params.filter) {
        case 'completed':
            filterTask = tasks.filter(arg=>arg.completed)
            break;
        default:
            filterTask = tasks
            break;
    }
        return(
            <section>
                <h1>Liste des t√¢ches</h1>
                <ul>
                    {
                        filterTask.map(
                            (arg) => <ToDo key={arg.id} task={arg} />
                        )
                    }
                </ul>
            </section>
        )
}

export default ToDoList;
