import React, {Component} from 'react';

class ToDo  extends Component {
   constructor(props){
       super(props)
       this.name = this.props.task.name
       this.state = {
           completed: this.props.task.completed
       }
   }

   toglleCompleted = () =>{
       this.setState(
           prevState => ({completed: !prevState.completed})
       )
   }

    render(){   
        return (
            <li className="list-group-item d-flex align-tiems-center">
                {this.name}
                <button className="btn ml-auto btn-outline-success" onClick={()=>this.toglleCompleted()}>&#x2713;</button>
            </li>
        )//end return in render()
    }//end render()

}//end ToDoClass

export default ToDo;
