import React, { Component, Fragment } from "react"
import axios from 'axios'

const Footer = () => {
    return(
    <nav className="navbar navbar-expand-sm bg-primary navbar-dark fixed-bottom">
        <span className="w-100 text-white text-center">&copy; Copyright Laza Novembre 2023</span>
    </nav> 
    )
}

const Home = () => {
    return(
        <section>
            <h1>Welcome into our website</h1>
        </section>
    )
}

class App extends Component{
    state = {
        datas: {}
    }

    componentDidMount(){
        axios.get('http://localhost:3001/listmembre')
            .then( res => {
                this.setState({datas: res.data})
            })
    }

    render(){
        return(
            <Fragment>
                <Home  />
                 <ul className="list-group">
                    {this.state.datas.map(item => <li key={item.id} className="list-group-item"> {item.nom} </li>)}
                </ul> 
                <Footer  />
            </Fragment>
        )
    }
        
}
export default App
