import React from 'react';
import ToDo from './ToDo';

const ToDoList = ({tasks, match}) =>{
    let filterTask;
    switch (match.params.filter) {
        case 'completed':
            filterTask = tasks.filter(task=>task.completed)
            break;

        default:
            filterTask = tasks
            break;
    }

    if(filterTask.length!==0){
        return(
            <>
                <h1 class="m-3">Liste de tâches</h1>
                <ul class="list-group m-3">
                    {
                        tasks.map((task)=><ToDo task={task} /> )
                    }
                </ul>
            </>
        )
    }else{
        return(
            <>
                <h1 class="m-3">Liste de tâches</h1>
                <ul class="list-group m-3">
                <li className="list-group-item d-flex align-tiems-center">Aucune tâche</li>
                </ul>
            </>
        )
    }


}

export default ToDoList
