import React, { Component, Fragment } from "react"
import Hello from "./Hello"
import Fonction from "./Fonction"
import "./style.css"

class App extends Component {
    state = {
        nom :"Rakoto",
        stat : false
    }

    Toggle =()=>{
        this.setState({stat: !this.state.stat})
        console.log(this.state.stat)
    }

    render() {
            
        return (
            <Fragment>
            <h1>{this.state.nom}</h1>
            <button className="btn btn-primary" onClick={this.Toggle}>{this.state.stat ? "off" : "on"}</button>
            <div id="boite" className={this.state.stat ? "on" : "on"}></div>
            </Fragment>
        )
    }
}

export default App
