import React, { Component, Fragment } from "react"
import axios from 'axios'

const Footer = () => {
    return(
    <nav className="navbar navbar-expand-sm bg-primary navbar-dark fixed-bottom">
        <span className="w-100 text-white text-center">&copy; Copyright Laza Novembre 2023</span>
    </nav> 
    )
}

const Home = ({datas}) => {
    return(
        <h1>Welcome into our website</h1>
    )
}

class App extends Component{
    constructor(){
        super()
        axios.get('http://localhost:3001/listmembre')
            .then(res => {
                this.setState({datas: res.data})
            })
    }

    componentDidMount(){
        axios.get('http://localhost:3001/listmembre')
            .then(res => {
                this.setState({datas: res.data})
            })
    }

    render(){
        if(this.state.datas){
            return(
                <Fragment>
                    <Home  datas={this.state.datas}  />
                    <Footer  />
                </Fragment>
            )
        }else{
            return(
                <h1>loading database ... </h1>
            )
        }
    }
}
export default App
