import React, { Component, Fragment } from "react"

const Footer = () => {
    return(
    <nav className="navbar navbar-expand-sm bg-primary navbar-dark fixed-bottom">
        <span className="w-100 text-white text-center">&copy; Copyright Laza Novembre 2023</span>
    </nav> 
    )
}

const Home = ({datas}) => {
    return(
        <h1>Welcome into our website</h1>
    )
}

class App extends Component{
    state = {
        datas: {}
    }

    componentDidMount(){
        try {
            fetch('http://localhost:3001/listmembre',{
                method: "get",
                headers: {"content-type": "text/json;charset=utf-8"}
            })
                .then(res => res.json())
                .then(json => this.setState({datas: json}))
        } catch (error) {
            window.alert(er)
        }
    }

    render(){
        return(
            <Fragment>
                <Home  datas={this.state.datas}  />
                <Footer  />
            </Fragment>
        )
    }
}
export default App
