import React, { Component }  from 'react';

class ToDo extends Component{
    constructor(props){
        super(props)
        this.label = this.props.task.name
        this.state = {
            completed: this.props.task.completed
        }
    }
    toggleCompleted = ()=> {
        this.setState(
            prevSate => ({completed: !prevSate.completed})
        )
    }
    render(){
        return(
            <li className="list-group-item d-flex align-tiems-center ">
                {this.label}
                <button className="btn btn-sm ml-auto btn-outline-success" onClick={this.toggleCompleted()}>&#x2713;</button>
            </li>
        )
    }
}

export default ToDo
