import React, { Component, Fragment } from "react"
import Hello from "./Hello"
import Fonction from "./Fonction"
import "./style.css"

class App extends Component{
    state = {
        etat: false
    }

    render(){
        const nom ="koto"
        const age = 25 
        
        const toggle = ()=>{
            this.state.etat = !this.state.etat
            console.log(this.state.etat)
            
        }
        return(
        <Fragment>
            <Hello nom={nom}/>
            <Fonction age={age}/>
            <button className="btn btn-primary" onClick={toggle}>
                interrupteur
            </button>

            <div  className={"boite off"}></div>
        </Fragment>
        )
    } 
}

export default App
