import React, { Fragment } from 'react';
import ToDo from './ToDo';

const ToDoList = ({tasks, match}) => {
    let filterTask
    switch (match.params.filter) {
        case 'completed':
            filterTask = tasks.filter(arg=>arg.completed)
            break;

        default:
            filterTask = tasks
            break;
    }


        return (
            <Fragment>
                <h1 className="m-3">Liste de t√¢ches</h1>
                <ul className="list-group m-3">
                    {
                        filterTask.map(
                            (arg)=> <ToDo task={arg} key={arg.id} />
                        )
                    }
                </ul>
            </Fragment>
        )



}

export default ToDoList;
