import React, { Component }  from 'react';

class ToDo extends Component{
    constructor(props){
        super(props)
        this.label = this.props.task.name
        this.id = this.props.task.id
        this.state = {
            completed: this.props.task.completed
        }
    }
    toggleCompleted = () => {
        this.setState(
            prevState=>({completed: !prevState.completed})
        )
        this.props.onToggleCompleted(this.id)
    }
    render(){
        return(
            <li className={"list-group-item d-flex align-tiems-center " + (this.state.completed===true ? "bg-success" : null)}>
                {this.label}
                <button className={"btn btn-sm ml-auto " + (this.state.completed===true ? "btn-outline-dark" : "btn-outline-success")} onClick={()=>this.toggleCompleted()}>&#x2713;</button>
            </li>
        )
    }
}

export default ToDo
