import React, { Fragment } from 'react';
import ToDo from './ToDo';

const ToDoList = ({tasks, match, onToggleCompleted}) => {
    let filterTask
    switch (match.params.filter) {
        case 'completed':
            filterTask = tasks.filter(arg=>arg.completed)
            break;

        default:
            filterTask = tasks
            break;
    }

    if(filterTask.length===0){
        return (
            <Fragment>
                <h1 className="m-3">Liste de tâches</h1>
                <ul className="list-group m-3">
                    <li>tsy misy azo raisina</li>
                </ul>
            </Fragment>
        )
    }else{
        return (
            <Fragment>
                <h1 className="m-3">Liste de tâches</h1>
                <ul className="list-group m-3">
                    {
                        filterTask.map(
                            (arg)=> <ToDo task={arg} key={arg.id} onToggleCompleted={onToggleCompleted} />
                        )
                    }
                </ul>
            </Fragment>
        )
    }


}

export default ToDoList;
