import React from 'react';
//import { render } from 'react-dom';
import ToDo from './ToDo'

const ToDoList = ({tasks, match, onToggleCompleted}) =>{
    let filterTasks
    switch (match.params.filter) {
        case 'completed':
            filterTasks = tasks.filter(arg => arg.completed)
            break;

        default:
            filterTasks = tasks
            break;
    }

    if(filterTasks.length===0){
        return(
            <section>
                <h1>Liste des tâches</h1>
                <ul>
                    <li>Aucune tâche</li>
                </ul>
            </section>
        )
    }else{
        return(
            <section>
                <h1>Liste des tâches</h1>
                <ul>
                {
                    filterTasks.map(
                        arg => <ToDo key={arg.id} task={arg} onToggleCompleted={onToggleCompleted} />
                    )
                }
                </ul>
            </section>
        )
    }
}

export default ToDoList;
