import React, { Component, Fragment } from "react"
import {BrowserRouter, Switch, Route, Link} from 'react-router-dom'

const Navbar = () => {
    return(
    <nav className="navbar navbar-expand-sm bg-primary navbar-dark">
        <div className="container-fluid">
          <button className="navbar-toggler" type="button" data-bs-toggle="collapse" data-bs-target="#collapsibleNavbar">
            <span className="navbar-toggler-icon"></span>
          </button>
          <div className="collapse navbar-collapse" id="collapsibleNavbar">
            <ul className="navbar-nav">
              <li className="nav-item">
                <Link className="nav-link" to="/">Home</Link>
              </li>
              <li className="nav-item">
                <Link className="nav-link" to="/listmembre">Lst of members</Link>
              </li>
              <li className="nav-item">
                <Link className="nav-link" to="/addmembre">Add membre</Link>
              </li>
            </ul>
          </div>
        </div>
    </nav> 
    )
}
const Footer = () => {
    return(
    <nav className="navbar navbar-expand-sm bg-primary navbar-dark fixed-bottom">
        <span className="w-100 text-white text-center">&copy; Copyright Laza Novembre 2023</span>
    </nav> 
    )
}

const Home = () => {
    return(
        <h1>Welcome into our website</h1>
    )
}

const Listmembre = (props) => {
    return(
        <ul className="list-group">
            <li className="list-group-item">First item</li>
            <li className="list-group-item">Second item</li>
            <li className="list-group-item">Third item</li>
      </ul> 
    )
}

class App extends Component{
    state = {
        datas: {}
    }
    render(){
        const url = 'http://localhost:4000/listmembre'
        fetch(url, {
            method: "GET",
            headers: {"Content-type": "text/json; charser=utf-8"}
        })
        .then(res => res.json())
        .then(json => this.setState({datas: json}))
        return(
            <BrowserRouter>
                <Fragment>
                    <Navbar  />
                    <Switch>
                        <Route exact path="/" component={Home}  />
                        <Route path="/listmembre" render={() => <Listmembre datas={this.state.datas} />} />
                    </Switch>
                    <Footer  />
                </Fragment>
            </BrowserRouter>
        )
    }
}
export default App
