import React, {Fragment} from "react";
import ToDo from "./ToDo";

export default function ToDoList({tasks, match, onToggleCompleted}) {
    let filteredTasks
	switch (match.params.filter) {
		case 'completed':
			filteredTasks = tasks.filter(task=>task.completed)
			break;
	
		default:
			filteredTasks = tasks
			break;
	}

	if(filteredTasks.length!==0){
		return(
			<Fragment>
				<h1>Liste des tâches</h1>
				<ul>
					{
						filteredTasks.map(arg => <ToDo key={arg.id} task={arg} onToggleCompleted={onToggleCompleted} />)
					}
				</ul>
			</Fragment>
		)
	}else{
		return(
			<Fragment>
				<h1>Liste des tâches</h1>
				<ul>
					<li className="list-group-item d-flex align-tiems-center">Aucune liste à afficher</li>
				</ul>
			</Fragment>
		)
	}
	
}
