import React, {Component, Fragment} from 'react';
import {BrowserRouter as Router, Switch, Route} from 'react-router-dom';
import ToDoList from './ToDoList';
import NavBar from './NavBar';
import Data from '../Data'
import AddTask from './AddTask';

class App  extends Component {
  constructor(props){
    super(props)
    this.state = {
      tasks: Data
    }
  }

  onToggleCompleted = (taskId) => {
	let taskToUpdate = this.state.tasks.find(arg=>arg.id===taskId)
	taskToUpdate.completed = !taskToUpdate.completed
	this.setState(
		prevState => prevState.tasks.map(
			task => {return task.id===taskId ? taskToUpdate : task}
		)
	)
  }

  render(){
    return(
      <Router>
        <Fragment>
          <Switch>
            <Route path="/add-task" render={()=><AddTask />} />
            <Route path="/:filter?" render={(props)=><ToDoList {...props} tasks={this.state.tasks} onToggleCompleted={this.onToggleCompleted} />} />
          </Switch>
          <NavBar />
        </Fragment>
      </Router>
    )
  }

}//end App class

export default App;
