import React from 'react';
//import { render } from 'react-dom';
import ToDo from './ToDo'

const ToDoList = ({tasks, match}) =>{
    let filterTasks
    switch (match.params.filter) {
        case 'completed':
            filterTasks = tasks.filter(arg => arg.completed)
            break;

        default:
            filterTasks = tasks
            break;
    }



    return(
        <section>
            <h1>Liste des t√¢ches</h1>
            <ul>
            {
                filterTasks.map(
                    arg => <ToDo key={arg.id} task={arg} />
                )
            }
            </ul>
        </section>
    )
}

export default ToDoList;
