import React, { Fragment } from 'react';
import ToDoList from './ToDoList';
import NavBar from './NavBar';
import AddTask from './AddTask';
import { BrowserRouter, Switch, Route} from 'react-router-dom';
import initialData from '../initialData';

class App extends React.Component{

  //atao anaty state ireo Data
  state = {
    tasks: initialData
  }

  onToggleCompleted = (taskId) =>{
    //troana ilay task ho ovaina 
    let tasktoUpdate = this.state.tasks.find(task=>task.id === taskId)
    //atao true na false
    tasktoUpdate.completed = !tasktoUpdate.completed
    //ovaina amzay
    this.setState(
      prevState => (
        prevState.tasks.map(tasks =>{
          return tasks.id === taskId ? tasktoUpdate : tasks
        })//map
      )//prevState
    )//setState

  }

  render(){
    return(
      <section id="todo">
        <BrowserRouter>
            <Fragment>
                <Switch>
                    <Route path="/add-task" component={AddTask} />
                    <Route path="/:filter?" render={(props) => <ToDoList {...props} tasks={this.state.tasks} onToggleCompleted={this.onToggleCompleted} />} />
                </Switch>
                <NavBar />
            </Fragment>
        </BrowserRouter>
      </section>
    );
  }
}

export default App;
