import React, {Fragment} from "react";
import ToDo from "./ToDo";

export default function ToDoList({tasks, match}) {
    let filteredTasks
	switch (match.params.filter) {
		case 'completed':
			filteredTasks = tasks.map(arg=>arg.completed)
			break;
	
		default:
			filteredTasks = tasks
			break;
	}
	
		return(
			<Fragment>
				<h1>Liste des tÃ¢ches</h1>
				<ul>
					{
						filteredTasks.map(arg => <ToDo key={arg.id} task={arg} />)
					}
				</ul>
			</Fragment>
		)
	
	
}
