import React, {Fragment} from "react";
import ToDo from "./ToDo";

export default function ToDoList({tasks, match}) {
    let filteredTasks
	switch (match.params.filter) {
		case 'completed':
			filteredTasks = tasks.filter(arg=>arg.completed)
			console.log("copl" + filteredTasks)
			break;
	
		default:
			filteredTasks = tasks
			console.log("tsy copl" + filteredTasks)
			break;
	}
    
    return(
        <Fragment>
            <h1>Liste des t√¢ches</h1>
            <ul>
                {
                    tasks.map(arg=><ToDo key={arg.id} task={arg} />)
                }
            </ul>
        </Fragment>
    )
}
