import React, {Component} from 'react';

class ToDo  extends Component {
    constructor(props){
        super(props)
        this.state = {
            completed: this.props.task.completed
        }
    }


    toggleCompleted = () => {
        this.setState(
            prevState=>({completed: !prevState.completed})
        )
        this.props.onToggleCompleted(this.props.task.id)
    }

    render(){   
        return (
            <li className={"list-group-item d-flex align-tiems-center " + (this.state.completed ? 'bg-success' : null)}>
                {this.props.task.name} 
                <button className={"btn ml-auto " + (this.state.completed ? 'btn-success' : 'btn-outline-success')} onClick={()=>this.toggleCompleted()}>&#x2713;</button>
            </li>
        )//end return in render()
    }//end render()

}//end ToDoClass

export default ToDo;
