import React from 'react';
import ToDo from './ToDo';

const ToDoList = ({tasks, match}) =>{
    let filterCompleted;
    switch (match.params.filter) {
        case 'completed' :
            filterCompleted = tasks.filter(task=>task.completed);
            break;

        default:
            filterCompleted = tasks;
            break;
    }



    return(
        <>  
            <h1 className="m-3">Liste de t√¢ches</h1>
            <ul className="list-group m-3">
                {
                    tasks.map((task)=><ToDo task={task} />)
                }
            </ul>
        </>
    )
}

export default ToDoList
