import React, {Fragment} from "react";
import ToDo from "./ToDo";

export default function ToDoList({tasks, match}) {
    let filterTask
    switch (match.params.filter) {
        case 'completed':
            filterTask = tasks.filter(arg=>arg.completed)
            break;
    
        default:
            filterTask = tasks
            break;
    }

    return (
    <Fragment>
        <h1>Liste des tÃ¢ches</h1>
        <ul>
            {
                filterTask.map(task => <ToDo key={task.id} task={task} />)
            }
        </ul>
    </Fragment>
    );
}
