import React, { Component }  from 'react';

class ToDo extends Component{
    constructor(props){
        super(props)
        this.state = {
            completed: this.props.task.completed
        }
        
    }

    
    
    render(){
        return(
            <li className={"list-group-item d-flex align-tiems-center " + (this.state.completed ? "bg-success" : null)}>
                {this.props.task.name}
                <button className={"btn btn-sm ml-auto " + (this.state.completed ? "btn-outline-dark" : "btn-outline-success")} onClick={()=>this.toggleCompleted()}>&#x2713;</button>
            </li>
        )
    }
}

export default ToDo
